const cds = require('../../../cds'), { exists, read } = cds.utils
const { readProject } = require('../../projectReader')
const { merge } = require('../../merge')

module.exports = class FileBasedMessagingTemplate extends require('../../plugin') {

  static help() {
    return 'messaging via file system'
  }

  async canRun() {
    const { isJava } = readProject()
    if (isJava) throw `'cds add file-based-messaging' is not available for Java yet`
    return true
  }

  static hasInProduction(env) {
    return env.requires?.messaging?.kind === 'file-based-messaging'
  }

  async run() {
    const project = readProject()
    const { configFile } = project
    project.shortcut = !exists(configFile) || typeof (await read(configFile)).cds?.requires?.messaging !== 'object'
    await merge(__dirname, 'files/package.json.hbs').into(configFile, { with: project })
  }
}
